values.value:
/* Creates 5 files related to the current Contact record */

recordId = recordId.replace(/,/g,"','");
let con = await soql(`select LastName from Contact where Id in ('${recordId}')`);
let files = con.map(c=>{
return  Array(5).fill(0).map((e,i)=>({
          VersionData: btoa(c.LastName + "-" + i),
          PathOnClient: "file.txt",
          Title: c.LastName + "-File-" + i,
          FirstPublishLocationId: c.Id
        }));
}).flat();

let fileIds = await dml.insert(files, "ContentVersion");
return fileIds;
